x = 0:pi/100:2*pi;y = sin(x);#plot(x,y)xlabel('x')ylabel('sin(x)')title('Plot of the Sine Function')#plot(x,y,'r--')plot(x,y,'g:*')hold ony2 = cos(x);plot(x,y2,':')legend('sin','cos')hold off[X,Y] = meshgrid(-2:.2:2);                                Z = X .* exp(-X.^2 - Y.^2);surf(X,Y,Z)#The first two inputs to subplot indicate the number of plots in each row and column. The third input specifies which plot is activet = 0:pi/10:2*pi;[X,Y,Z] = cylinder(4*cos(t));subplot(2,2,1); mesh(X); title('X');subplot(2,2,2); mesh(Y); title('Y');subplot(2,2,3); mesh(Z); title('Z');subplot(2,2,4); mesh(X,Y,Z); title('X,Y,Z');