clcclearA = 3.1416;str = octaveString.string(A);isequal(str,'3.1416')A = [256 3.1416 8.9e-3];str = octaveString.string(A);#assert(str,'2563.14160.0089')str = octaveCompose.compose("%9.7f",pi);assert(str,'3.1415927','specify the format of the output text using the compose')A = [256 3.1416 8.9e-3];str = octaveCompose.compose("%5.2e",A);X = linspace(0,2*pi);Y = sin(X);plot(X,Y)xlabel("X")ylabel("Y")freq = octaveString.string(1/(2*pi));titleStr = octaveAppend.append("Sine Wave, Frequency = ",freq," Hz");assert(titleStr,"Sine Wave, Frequency = 0.15915 Hz")register = 0b10010110;register = bitset(register,5,0);binStr = dec2bin(register);isequal(binStr,'10000110')d = octaveDatetime.datetime('now');assert(d(1:11),'31-May-2020 00:09:48'(1:11))str = octaveString.string(d);D = octaveSeconds.seconds(1:5);sec='sec';assert(D,{1,2,3,4,5;'1 sec','2 sec','3 sec','4 sec','5 sec'})iscell(D)str = octaveString.string(D);isequal(str,['1 sec','2 sec','3 sec','4 sec','5 sec'])