clcclear# A structure is a data type that groups related data using data containers called fields.patient(1).name = 'John Doe';patient(1).billing = 127.00;patient(1).test = [79, 75, 73; 180, 178, 177.5; 220, 210, 205];amount_due = patient(1).billing;isequal(patient(1).billing,amount_due,127,127.0,127.00)isequal(size(patient),[1 1])patient(2).name = 'Ann Lane';patient(2).billing = 28.50;patient(2).test = [68, 70, 68; 118, 118, 119; 172, 170, 169];isequal(patient(2).billing,28.50,28.5)isequal(size(patient),[1 2])patient(3).name = 'New Name';foo=patient(3);isequal(typeinfo(foo),'scalar struct')isequal(foo.name,'New Name')isequal(foo.billing,[])isequal(foo.test,[])isequal(size(foo),[1 1])isequal(size(patient),[1 3])bar(patient(1).test)title(['Test Results for ', patient(1).name])