clcclear#octaveMersenneTwister.rng ('default')A = randi(25,5);x = rand(1,5);A = ~mod(A,2);isequal(any(A(:)),all([1 1]),1)isequal(all(~A(:)),0)B=[];for i=1:rows(A)  B(i,1)=A(i,i);  endforisequal(diag(A),B)B=[];for i=1:rows(A)-1  B(i,1)=A(i,i+1);  endfor  isequal(B,diag(A,1))isequal(diag([1 2 3],1)(2,3),2)isequal(diag(1),1)islogical(any(diag(A)) || any(diag(A,1)))