clcclearA = [0 1 2; 0.5 0 1; 2 1 0]Asave = A;% Scale A by power of 2 so that its norm is < 1/2 .[f,e] = log2(norm(A,'inf'));s = max(0,e+1);A = A/2^s;% Pade approximation for exp(A)X = A;c = 1/2;E = eye(size(A)) + c*A;D = eye(size(A)) - c*A;q = 6;p = 1;for k = 2:q   c = c * (q-k+1) / (k*(2*q-k+1));   X = A*X;   cX = c*X;   E = E + cX;   if p     D = D + cX;   else     D = D - cX;   end   p = ~p;endE = D\E;% Undo scaling by repeated squaringfor k = 1:s    E = E*E;endE1 = EA = Asave;% Taylor series for exp(A)E = zeros(size(A));F = eye(size(A));k = 1;while norm(E+F-E,1) > 0   E = E + F;   F = A*F/k;   k = k+1;endE2 = EA = Asave;[V,D] = eig(A);E = V * diag(exp(diag(D))) / V;E3 = EE = expm(Asave);err1 = E - E1err2 = E - E2err3 = E - E3A = [-147 72; -192 93];E1 = expm(A)E2 = expm(A)E3 = expm(A)A = [-1 1; 0 -1];E1 = expm(A)