function detVal=getDet(A)  detVal=0;  permuteMat=getPermute(A);  for i=1:rows(permuteMat)    currentRow=permuteMat(i,:);    mat=[];    for m=1:columns(currentRow)      mat(m)=A(m,currentRow(m));    endfor    inverseNum=getInverseNum(currentRow);    rowProd=prod(mat);    if(mod(inverseNum,2))    detVal-=rowProd;    else      detVal+=rowProd;    endif  endforendfunctionfunction permuteMat=getPermute(mat)  permuteMat=[];  cols=columns(mat);  permuteMat=perms (1:cols);  endfunction