function detVal=getDet(A)  detVal=0;  permuteMat=getPermute(A);  for i=1:rows(permuteMat)    currentRow=permuteMat(i,:);    indRow=1;    mat=[];    for m=1:columns(currentRow)      mat(m)=A(indRow,currentRow(m));      indRow++;    endfor    row=getInverseNum(currentRow);    if(mod(row,2))    detVal-=prod(mat);    else      detVal+=prod(mat);    endif  endforendfunctionfunction permuteMat=getPermute(mat)  permuteMat=[];  cols=columns(mat);  permuteMat=perms (1:cols);  endfunction