clcclear allA=ones(2,3);B=[0 0 0;-1 1 1];C=zeros(3,3);isequal(A+B,plus(A,B),[1 1 1;0 2 2])isequal(A-B,minus(A,B),[1 1 1;2 0 0])fail('A-C','operator -: nonconformant arguments')isequal(A+0,A)isequal(A+(-A),zeros(size(A)))isequal(A+B-B,A)isequal((A+B)*2,A*2+B*2)isequal((2+3)*A,A*2+A*3)function product=matProduct(mat1,mat2)  product=zeros(rows(mat1),columns(mat2));  if(!any(mat1)||!any(mat2))  returnendifif(columns(mat1)==rows(mat2))for i=1:rows(mat1)  for n=1:columns(mat2)    for m=1:columns(mat1)      product(i,m)+=mat1(i,m)*mat2(m,n);    endfor  endforendforelse  error('nonconformant arguments')endifendfunctionfail('A*B','nonconformant arguments')fail('matProduct(A,B)','nonconformant arguments')isequal(A.*B,[0 0 0;-1 1 1])D=eye(3);isequal(A*D,matProduct(A,D),ones(2,3))
isequal(zeros(3,4)*ones(4,5),matProduct(zeros(3,4),ones(4,5)))function product=getEquationSetMat(coefs,unknownQuantity)mat1=coefs;mat2=unknownQuantity;product={'',''};if(!any(mat1)||!any(mat2))returnendifif(columns(mat1)==rows(mat2))for i=1:rows(mat1)for m=1:columns(mat1)  currentCoef=mat1(i,m);  if(currentCoef==1)  coef='';  elseif(currentCoef==-1)  coef='-';  else    coef=num2str(currentCoef);  endif  item=strcat(coef,mat2{m});  if(currentCoef<0||isempty(product{i}))  product{i}=strcat(product{i},item);  else    product{i}=strcat(product{i},'+',item);  endifendforendforelseerror('nonconformant arguments')endif
endfunctionequations1=getEquationSetMat([1 -1;1 1],{'y1';'y2'});
isequal(equations1,{'y1-y2','y1+y2'})equations2=getEquationSetMat([1 1 2;1 -2 1],{'z1';'z2';'z3'});isequal(equations2,{'z1+z2+2z3','z1-2z2+z3'})