clcclear allA=rand(2);C=rand(2,3);k1=2;k2=3;fail('C^k1','only square matrix arguments are permitted and one argument must be scalar')assert(A^k1*A^k2,A^(k1+k2),6e-15)assert(A^k1^k2,A^(k1*k2))B=rand(2);isequal(all((A*B)^2!=A^2*B^2),[1 1])A=ones(3,1);B=(1:3);BA=6;isequal(B*A,BA)AB=[1:3;1:3;1:3];isequal(A*B,AB)isequal((A*B)^2,A*B*A*B,A*(B*A)*B,6*A*B,6*AB)pow=10;isequal((A*B)^pow,(B*A)^((20-2)/2)*A*B)isequal(transpose(A*B),transpose(B)*transpose(A))