global fname#isnan(newtroot('plus',10))assert(feval ("acos", -1),3.1416,7e-5)f = @exp; isequal(feval (f, 1),feval('exp',1),e)assert(e,2.7183,2e-5)run /Users/zhanghongliang/Documents/Octave/Evaluation/Evaluation.mrun('/Users/zhanghongliang/Documents/Octave/Evaluation/Evaluation.m')run Evaluation.mrun ('Evaluation.m')fail("run ('Evaluation1.m')",'file SCRIPT must exist and be a valid Octave scriptfile')function result = newtroot (fname, x)  # usage: newtroot (fname, x)  #  fname : a string naming a function f(x).   #  x : initial guess  delta = tol = sqrt (eps);  maxit = 200;  fx = feval (fname, x);  for i = 1:maxit    if (abs (fx) < tol)      result = x;      return;      else        fx_new = feval (fname, x + delta);        deriv = (fx_new - fx) / delta;        x = x - fx / deriv;        fx = fx_new;      endif     endfor    result = x;    endfunction    