clcclear all#When called with one output and no inputs, return an options structure with all valid option parameters initialized to []. validParams=optimset();isequal(typeinfo(validParams),'scalar struct')#Return a cell array of strings with the names of the fields in the specified input.fields=fieldnames(validParams );isequal(typeinfo(fields),'cell')fail('cell2mat(fields)','cat: dimension mismatch')str='';for k = 1:numel(fields)   str=strcat(str,fields{k},', ');endfieldsFun=[cellfun(@(x)strcat(x,', '),fields,'UniformOutput',false){:}];isequal(str(1:end-1),fieldsFun(1:end-1),'AutoScaling,ComplexEqn,Display,FinDiffType,FunValCheck,GradObj,Jacobian,MaxFunEvals,MaxIter,OutputFcn,TolFun,TolX,TypicalX,Updating')#When called with a list of parameter/value pairs, return an options structure with only the named parameters initialized. max_iterations = 30; options = optimset('MaxIter', max_iterations);isequal( typeinfo(options),'scalar struct')isequal(options.MaxIter,30)