clcclear allx=1;y=2;n=[1,2];m=[3,4];p=[5,6,7];assert1(x + y,x.+y,plus(x,y),3);assert1(m + n,[4,6])assert1(m .+ n,[4,6])fail('m+.n','syntax error')assert1(m -n,minus(m,n),[2,2]);fail('m-p','nonconformant arguments')#If both operands are matrices, the number of rows and columns of both must agree, or they must be broadcastable to the same shape.fail('m*n','nonconformant arguments')#The number of columns of x must agree with the number of rows of y.q=[1,2;3,4];assert1(m*q,mtimes(m,q),[3*1+4*3,3*2+4*4],[15,22]);assert1(m.*q,times(m,q),[3*1,4*2;3*3,4*4],[3,8;9,16]);#Element-by-element multiplicationassert1(x/y,x./y,(inv (y') * x')',mrdivide(x,y),rdivide(x,y),.5);#Right divisionassert1(x\y,x.\y,inv (x) * y,inv (x) .* y,mldivide(x,y),ldivide(x,y),2);assert1(x^y,x**y,x.^y,x.**y,mpower(x,y),power(x,y),1);assert1(-x,uminus(x),-1)assert1(+x,uplus(x),+1,1)assert1(x',x.',ctranspose(x),transpose(x),1)