clcclear allfunction flipMat=flipLeftToRight(mat)  flipMat=[];  for i=1:rows(mat)    flipMat(i,:)=flip(mat(i,:));  endforendfunctionfunction flipMat=flipUpsideDown(mat)  flipMat=[];  for i=1:columns(mat)    flipMat(:,i)=flip(mat(:,i));  endforendfunctionfunction flipMat=flipAcrossDim(mat,dim=1)  flipMat=[];  if(isrow(mat))  for i=0:length(mat)-1    flipMat(i+1)=mat(end-i);  endfor  elseif(iscolumn(mat))  for i=0:length(mat)-1    flipMat(i+1,1)=mat(end-i);  endfor  elseif(dim==1)  for i=0:rows(mat)-1    flipMat(i+1,:)=mat(end-i,:);  endfor  else  for i=0:columns(mat)-1    flipMat(:,i+1)=mat(:,end-i);  endfor  endifendfunctionA=[1, 2; 3, 4];isequal(fliplr (A),flipLeftToRight(A),flip(A,2),flipAcrossDim(A,2),[2 1;4 3])isequal(flipud (A),flipUpsideDown(A),flip (A),flipAcrossDim(A),[3 4;1 2])B=[1  2  3  4];isequal(flip (B),flipAcrossDim(B),[4 3 2 1])C=[1; 2; 3; 4];isequal(flip (C),flipAcrossDim(C),[4;3;2;1])