## -*- texinfo -*-## @deftypefn  {Function File} {} datetime ()## @deftypefnx {Function File} {} datetime (@var{str})## Create str datetime object representing the datetime#### @example## a0 + a1 * x + a2 * x^2 + @dots{} + an * x^n## @end example#### @noindent## from str vector of coefficients [a0 a1 a2 @dots{} an].## @end deftypefnfunction p = datetime (str,mon=0,day=0)  if (nargin == 0)    p.poly = [0];    p = class (p, "datetime");  elseif (nargin == 1)    if (strcmp (class (str), "datetime"))      p = str;    elseif (isvector (str) && isreal (str))#      p.poly = str(:).';      p=_datetime(str,mon,day)#      p = class (p, "datetime");    else      error ("datetime: expecting real vector");    endif  else    print_usage ();  endifendfunctionfunction date= _datetime(str,mon,day)  if(strcmp(str,'now')==1)  date = datestr(now());  elseif(strcmp(str,'today')==1)  date=datestr(today());  else    date=[];     for i=1:length(mon)      rowStr=num2str([i,day,str],'%d/');      dateFull=strcat(rowStr)(1:end-1);      date(i,:)=datestr(datenum (dateFull,2));#'each row in str character matrix is str separate string    endfor  endifend