function varargout=assert1(varargin)  try    len=numel(varargin);    if(len>2&&isscalar(varargin{len}))    tol=varargin{len};    len=len-2;    else    tol=0;    len=len-1;    endif    for i=1:len-1      val1=varargin{i};      val2=varargin{i+1};      assert(val1,val2,tol);      #  err=0      varargout{1}=1;    endfor    catch(e)    varargout{1}=0;    varargout{2}=e;    error(e)  end_try_catchendfunction