function varargout=assert1(varargin)  try    len=numel(varargin);    last=varargin{len};    if(isstruct(last)&&isfield(last,'tol'))    tol=last.tol;    len=len-2;    else    tol=10^-output_precision;#Query or set the internal variable that specifies the minimum number of significant figures to display for numeric output.     len=len-1;    endif    for i=1:len      val1=varargin{i};      val2=varargin{i+1};      assert(val1,val2,tol);      #  err=0      varargout{1}=1;    endfor    catch(e)    varargout{1}=0;    varargout{2}=e;    error(e)  end_try_catchendfunction