function [matRank,isRowFullRank,isColFullRank]=getRank(mat)  if(mat==0)  matRank=0;  isRowFullRank=isColFullRank=0;  return  endif  [row,col]=size(mat);  if(row<col)  num=row;  else    num=col;  endif  v=1:num;  for i=1:num    choose=nchoosek(v,i);    row=rows(choose);    if(row>1)    prob=nchoosek(1:row,2);    else      prob=[1,1];    endif    for m=1:rows(prob)      num1=choose(prob(m,:)(1),:);      num2=choose(prob(m,:)(2),:);      [~,~,cofactor]=getNOrderCofactor(mat,num1,num2);      [~,~,cofactor1]=getNOrderCofactor(mat,num2,num1);      if(cofactor||cofactor1)      matRank=i;      break    endif  endforendforif(rows(matRank)==row)isRowFullRank=1;elseisRowFullRank=0;endifif(columns(matRank)==col)isColFullRank=1;elseisColFullRank=0;endifendfunction